mapper.update:
UPDATE order_client oc, order_item oi
SET oc.status = 4, oc.update_time = now()
WHERE oc.id = oi.order_id AND oc.id = #{id}

mapper.select:
SELECT oc.id, oc.no, c.client_id, oc.order_time, oc.address, oc.total_money, oc.status, oc.pay_time, oc.receive_time, oc.finish_time, oc.update_time, oi.id, oi.goods_id, oi.num, oi.price, oi.status, g.name AS goods_name, g.update_time AS goods_update_time, g.sale_price, g.unit_id, g.status AS goods_status, g.pur_status, pr.pro_num, g.stock_lower_limit, g.stock_upper_limit, c.client_name, mu.name AS unit_name FROM order_client oc LEFT JOIN order_item oi ON oc.id = oi.order_id LEFT JOIN goods g ON oi.goods_id = g.id LEFT JOIN client c ON oc.client_id = c.id LEFT JOIN measuring_unit mu ON oi.unit = mu.id LEFT JOIN product_report pr ON pr.pro_name = g.name WHERE oc.status IN (0,1,2,3)
AND oc.no like concat('%',#{oc.no},'%')
AND c.client_id = #{oc.clientName}
AND oc.status = #{oc.status}
AND oc.pay_time BETWEEN #{oc.selectTime[0]} AND #{oc.selectTime[1]}

SELECT count(distinct oc.id) FROM order_client oc LEFT JOIN order_item oi ON oc.id = oi.order_id LEFT JOIN goods g ON oi.goods_id = g.id LEFT JOIN client c ON oc.client_id = c.id LEFT JOIN measuring_unit mu ON oi.unit = mu.id WHERE oc.status IN (0,1,2,3)
AND oc.no like concat('%',#{no},'%')
AND c.client_name like concat('%',#{clientName},'%')
AND oc.status = #{status}
AND oc.pay_time BETWEEN #{selectTime[0]} AND #{selectTime[1]}

SELECT oc.id,oc.total_money,c.client_name
FROM `order_client` oc
left join client c
on oc.client_id=c.id


     and c.client_name= #{name}



            and oc.order_time >= #{item1}


            and oc.order_time <= #{item1}
